if (flow.get('$parent.SYSTEM_SOUNDS_VARIABLE')) {

    switch (msg.topic) {
        case "SW_1":
            msg.payload = `Air Pump is ${msg.payload ? "ON" : "OFF"}`;
            break;
        case "SW_2":
            msg.payload = `Lighting is ${msg.payload ? "ON" : "OFF"}`;
            break;
        case "SW_3":
            msg.payload = `"Water Filter is ${msg.payload ? "ON" : "OFF"}`;
            break;
        case "SW_4":
            msg.payload = `Heater is ${msg.payload ? "ON" : "OFF"}`;
            break;
        case "automaticModeSwitch":
            msg.payload = `Automatic mode ${msg.payload ? "enabled" : "disabled"}`;
            break;
        case "systemSoundsSwitch":
            msg.payload = `System sound ${msg.payload ? "enabled" : "disabled"}`;
            break;
        case "tempretureSlider":
            var degrees = msg.payload;
            msg.payload = "Temperature is set to " + degrees + " degrees";
            break;
        default:
            break;
    }

    return msg;
}

if (msg !== undefined && msg.topic !== undefined) {

    var schedulerOutput = '0';

    switch (msg.topic) {
        case 'Air Pump':
            schedulerOutput = '0';
            break;
        case 'Lighting':
            schedulerOutput = '1';
            break;
        case 'Water Filter':
            schedulerOutput = '2';
            break;
        case 'Heater':
            schedulerOutput = '3';
            break;
    }

    let availableShedules = flow.get('$parent.ENABLED_SHEDULE_OUTPUTS_VARIABLE') || [];
    let hasShedule = availableShedules.indexOf(schedulerOutput) >= 0;

    if (hasShedule) {
        msg.topic = schedulerOutput
        return msg;
    }
}

var tempretureSetTo = msg.payload

if(msg.payload >= tempretureSetTo){
    /* stop heater */
    msg.payload = false;
}
else if (msg.payload < tempretureSetTo){
    /* start heater */
    msg.payload = true;
}

return msg;

var value = msg.payload.temperature;

msg.payload = value.toFixed(2);

return msg;

var time = new Date();
var year = time.getFullYear();
var month = time.getMonth()+1;
var date1 = time.getDate();
var hour = time.getHours();
var minutes = time.getMinutes();
var seconds = time.getSeconds();

msg.payload = (year + "-" + month + "-"+date1+" "+hour+":"+minutes+":"+seconds + " UTC");




return msg;

// Uptime human readable 
function timeConversion(millisec) {
    var seconds = (millisec / 1000).toFixed(1);
    var minutes = (millisec / (1000 * 60)).toFixed(1);
    var hours = (millisec / (1000 * 60 * 60)).toFixed(1);
    var days = (millisec / (1000 * 60 * 60 * 24)).toFixed(1);

    if (seconds < 60) {
        return seconds + " Sec";
    } else if (minutes < 60) {
        return minutes + " Min";
    } else if (hours < 24) {
        return hours + " Hrs";
    } else {
        return days + " Days"
    }
}

// Free RAM/FS percentage
function calcPercentageFromTotal(valueToConvert, totalValue) {
    var value = ((valueToConvert / totalValue) * 100);
    return Math.round( value * 1e1 ) / 1e1;
}

msg.payload.uptimeAsString = timeConversion(msg.payload.uptime * 1000);
msg.payload.freeRAMPercentage = calcPercentageFromTotal(msg.payload.ram_free, msg.payload.ram_size);
msg.payload.freeFSPercentage = calcPercentageFromTotal(msg.payload.fs_free, msg.payload.fs_size);

// Dont need the whole WIFI obj
msg.payload.wifi = msg.payload.wifi.sta_ip;

return msg;

var topic = msg.topic;
var payload = msg.payload; 

if (payload) {
    msg.payload = { [topic]: 0 };
}
else {
    msg.payload = { [topic]: 1 };
}

flow.set(topic, payload);

return msg;

